import { Component, OnInit, Renderer2 } from "@angular/core";

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.scss"],
})
export class AppComponent implements OnInit {
  navHamburger: any;
  navLinks: any;

  constructor(private renderer: Renderer2) {}

  title = "SoGen_Front";

  ngOnInit() {
    this.navHamburger = document.querySelector(".menu-hamburger");
    this.navLinks = document.querySelector(".nav-links");
  }

  ngAfterViewInit() {
    this.navHamburger.addEventListener("click", () => {
      this.navLinks.classList.toggle("mobile-menu");
    });
  }
}
<body>
  <nav class="navbar">
    <a href="#" class="logo">SoGeneration</a>
    <div class="nav-links">
      <ul>
        <li><a href="#active">Home</a></li>
        <li><a href="#">Joueurs</a></li>
        <li><a href="#">Equipes</a></li>
        <li><a href="#">Contact</a></li>
      </ul>
    </div>
    <img
      src="../assets/Img/icons8-menu-50.png"
      alt="menu hamburger"
      class="menu-hamburger"
    />
  </nav>
  <header></header>
</body>
